import React,{Component} from "react";
import styles from "./index.scss";
import {connect} from "react-redux";
import Navbar from "@/Components/Common/Navbar";


class Cart extends Component {
	constructor(){
		super();
		this.state={
			isAll:true,
			isLittleAll:[],
			isChoose:[],
			boxList:[],
			total:0.00,
			balance:true,
			isEdit:true,
			isAllShow:'',
			isSkuId:[],
		}
		this.count =[];
		this.sum =[];
		this.sumss=0;
		this.summ=0;
		this.totals=0.00;
		this.del=[];
	}

	componentDidMount(){
		var session = sessionStorage.getItem('username')
		axios.get(`/api/getcart?username=${session}`).then(res=>{
			
			if(res.data.data.length==0){
				this.setState({
				isAllShow:false
			})
			}else{
					this.setState({
					isAllShow:true
				})
			}

			for(let i=0;i<res.data.data.length;i++){
				this.sum.push(true);
				this.count.push([]);
				this.del.push([]);
				for(let j=0;j<res.data.data[i].cartDetails.length;j++){
					this.count[i].push(true)
					this.del[i].push(res.data.data[i].cartDetails[j].skuId)
				}
			}
			this.setState({
				boxList:res.data.data,
				isChoose:this.count,
				isLittleAll:this.sum,
				isSkuId:this.del
			})
		})

	}

	render(){
		return <div id={styles.cart}>
		<div className={styles.cartHead}>
			<div className={styles.cartName}>
				<span>购物车</span>
			</div>
			<div className={styles.edit} onClick={this.edit.bind(this)}>
				<span>{this.state.isEdit?"编辑":"完成"}</span>
			</div>
		</div>
		{this.state.isAllShow?<div className={styles.cartContainer}>
		{this.state.boxList.map((item,index)=>
			<div className={styles.cartBox} key={item._id}>
				<div className={styles.cartBoxHead}>
					<div className={styles.cartBoxImg} onClick={this.cartBoxImg.bind(this,index)}>
						<img ref={item._id} src="data:image/png;base64," />
					</div>
					<div className={styles.cartBoxTitle}>
						<span>
							{item.title}
						</span>
					</div>
				</div>
				{item.cartDetails.map((items,indexs)=>
					<div className={styles.cartBoxBody} key={items.skuId+"box"}>
						<div className={styles.cartBoxChoose} onClick={this.cartBoxChoose.bind(this,index,indexs,items.price)}>
							<img  ref={items.skuId+"img"} src="data:image/png;base64," />
						</div>
						<div className={styles.cartBoxDetail}>
							<div className={styles.cartBoxDetailImg}>
								<img src={items.picture} />
							</div>
							<div className={styles.cartBoxDetailContent}>
								<div className={styles.cartBoxDetailContentDescribe}>
									<p className={styles.cartBoxDetailContentDescribeP1}>{items.productName}</p>
									<p className={styles.cartBoxDetailContentDescribeP2}>{items.productAttr}</p>
								</div>
								<div className={styles.cartBoxDetailContentPrice}>
									<p>¥{items.price}</p>
								</div>
							</div>
						</div>
						<div className={styles.cartBoxCount} ref={items.skuId+"Box"}>
							<div className={styles.cartBoxCountBox1} onClick={this.cartBoxCountBox1.bind(this,index,indexs)}>
								<img src="data:image/png;base64," />
							</div>
							<div className={styles.cartBoxCountBox2}>
								<span ref={items.skuId}>{items.quantity}</span>
							</div>
							<div className={styles.cartBoxCountBox3} onClick={this.cartBoxCountBox3.bind(this,index,indexs)}>
								<img src="data:image/png;base64," />
							</div>
						</div>
					</div>
				)}
				
			</div>
		)}
		</div>:<div className={styles.cartContainerDisappear}>
			<div className={styles.emptyBox}>
				<div className={styles.emptyBoxPic}>
					<img src="data:image/png;base64," />
				</div>
				<p className={styles.emptyBoxText}>
					购物车还空着呢，快去挑选商品吧
				</p>
			</div>
		</div>}
		{this.state.isAllShow?<div className={styles.cartFoot} ref="allView">
			<div className={styles.checkAll} onClick={this.checkAll.bind(this)}>
				<div  className={styles.checkAllLeft}>
					<div className={styles.checkAllImg}>
						<img ref="checkImg" src="data:image/png;base64," />
					</div>
				</div>
				<div  className={styles.checkAllRight}>
					<span>全选</span>
				</div>
			</div>
			<div className={styles.statement}>
				<div className={this.getClassName()} onClick={this.end.bind(this)}>
					<span>{this.state.isEdit?"去结算":"删除"}</span>
				</div>
				<div className={styles.statementRight} ref="statement">
					<p>¥{this.state.total}</p>
				</div>
			</div>
		</div>:null}
		<Navbar/>
		</div>
	}
	getClassName(){
		return this.state.balance?styles.statementLeft:styles.statementLeftBlink
	}
	checkAll(){
		if(this.state.isAll){
			this.refs.checkImg.setAttribute('src', 'data:image/png;base64,')
			for(let i=0;i<this.state.boxList.length;i++){
				this.sum[i]=false;
				this.refs[this.state.boxList[i]._id].setAttribute('src', 'data:image/png;base64,')
				for(let j=0;j<this.state.boxList[i].cartDetails.length;j++){
					this.count[i][j]=false;
					this.refs[this.state.boxList[i].cartDetails[j].skuId+"img"].setAttribute('src', 'data:image/png;base64,')
					this.totals += this.state.boxList[i].cartDetails[j].price*this.refs[this.state.boxList[i].cartDetails[j].skuId].innerHTML

				}
			}

			this.setState({
				isAll:!this.state.isAll,
				isLittleAll:this.sum,
				isChoose:this.count,
				total:parseFloat(this.totals),
				balance:false
			})
		}else{
			this.refs.checkImg.setAttribute('src', 'data:image/png;base64,')
			for(let i=0;i<this.state.boxList.length;i++){
				this.sum[i]=true;
				this.refs[this.state.boxList[i]._id].setAttribute('src', 'data:image/png;base64,')
				for(let j=0;j<this.state.boxList[i].cartDetails.length;j++){
					this.count[i][j]=true;
					this.refs[this.state.boxList[i].cartDetails[j].skuId+"img"].setAttribute('src', 'data:image/png;base64,')		
					this.totals -= this.state.boxList[i].cartDetails[j].price*this.refs[this.state.boxList[i].cartDetails[j].skuId].innerHTML
				}
			}	

			this.setState({
				isAll:!this.state.isAll,
				isLittleAll:this.sum,
				isChoose:this.count,
				total:parseFloat(this.totals),
				balance:true
			})
		}
		
	}
	cartBoxImg(index){
		if(this.sum[index]){
			this.refs[this.state.boxList[index]._id].setAttribute('src', 'data:image/png;base64,')
			for(let i=0;i<this.state.boxList[index].cartDetails.length;i++){
				this.refs[this.state.boxList[index].cartDetails[i].skuId+"img"].setAttribute('src', 'data:image/png;base64,')
				this.totals += this.state.boxList[index].cartDetails[i].price*this.refs[this.state.boxList[index].cartDetails[i].skuId].innerHTML
				if(this.count[index][i]==false){
					this.totals -= this.state.boxList[index].cartDetails[i].price*this.refs[this.state.boxList[index].cartDetails[i].skuId].innerHTML
				}
				this.count[index][i]=false;
			}
			
			this.sum[index]=false;
			this.setState({
				isLittleAll:this.sum[index],
				isChoose:this.count,
				total:parseFloat(this.totals),
				balance:false
			})
			for(let k=0;k<this.state.boxList.length;k++){
				if(this.sum[k]==false){
					this.summ++
				}
			}
			if(this.summ==this.sum.length){
				this.refs.checkImg.setAttribute('src', 'data:image/png;base64,')
				this.setState({
					isAll:false
				})

			}
			this.summ=0;
		}else{

			this.refs[this.state.boxList[index]._id].setAttribute('src', 'data:image/png;base64,')
			for(let j=0;j<this.state.boxList[index].cartDetails.length;j++){
			this.refs[this.state.boxList[index].cartDetails[j].skuId+"img"].setAttribute('src', 'data:image/png;base64,')		
			this.totals -= this.state.boxList[index].cartDetails[j].price*this.refs[this.state.boxList[index].cartDetails[j].skuId].innerHTML
			this.count[index][j]=true;
			}
			
			this.sum[index]=true;
			this.setState({
				isLittleAll:this.sum[index],
				isChoose:this.count,
				total:parseFloat(this.totals)
			})
			for(let k=0;k<this.state.boxList.length;k++){
				if(this.sum[k]==false){
					this.summ++
				}
			}
			if(this.summ!=this.sum.length){
				this.refs.checkImg.setAttribute('src', 'data:image/png;base64,')

				this.setState({
					isAll:true
				})
			}
			if(this.summ==0){
				this.setState({
					balance:true
				})
			}
			this.summ=0;
		}
		
	}
	cartBoxChoose(index,indexs,price){
		if(this.count[index][indexs]){
			this.refs[this.state.boxList[index].cartDetails[indexs].skuId+"img"].setAttribute('src', 'data:image/png;base64,')
			this.count[index][indexs]=false;
			this.totals+=price*this.refs[this.state.boxList[index].cartDetails[indexs].skuId].innerHTML
			this.setState({
				isChoose:this.count,
				total:parseFloat(this.totals),
				balance:false
			})
			for(let i=0;i<this.state.boxList[index].cartDetails.length;i++){
				
					if(this.count[index][i]==false){
						this.sumss++					
					}
			}
			if(this.sumss==this.state.boxList[index].cartDetails.length){
				this.refs[this.state.boxList[index]._id].setAttribute('src', 'data:image/png;base64,')
				this.sum[index]=false
				this.setState({
					isLittleAll:this.sum[index]
				})
				for(let k=0;k<this.state.boxList.length;k++){
					if(this.sum[k]==false){
						this.summ++
					}
				}
				if(this.summ==this.sum.length){
					this.refs.checkImg.setAttribute('src', 'data:image/png;base64,')
					this.setState({
						isAll:false
					})
				}
			}
			this.sumss=0;
			this.summ=0;
		}else{
			this.refs[this.state.boxList[index].cartDetails[indexs].skuId+"img"].setAttribute('src', 'data:image/png;base64,')		
			this.count[index][indexs]=true;
			this.totals-=price*this.refs[this.state.boxList[index].cartDetails[indexs].skuId].innerHTML
			this.setState({
				isChoose:this.count,
				total:this.totals
			})
			for(let i=0;i<this.state.boxList[index].cartDetails.length;i++){
				
					if(this.count[index][i]==false){
						this.sumss++					
					}
			}
			if(this.sumss==0){
				this.setState({
					balance:true
				})
			}
			if(this.sumss!=this.state.boxList[index].cartDetails.length){
				this.refs[this.state.boxList[index]._id].setAttribute('src', 'data:image/png;base64,')
				this.refs.checkImg.setAttribute('src', 'data:image/png;base64,')

				this.sum[index]=true
				this.setState({
					isLittleAll:this.sum[index],
					isAll:true
				})
				for(let k=0;k<this.state.boxList.length;k++){
					if(this.sum[k]==false){
						this.summ++
					}
				}
				if(this.summ!=this.sum.length){
					this.refs.checkImg.setAttribute('src', 'data:image/png;base64,')

					this.setState({
						isAll:true
					})
				}
				this.sumss=0;
				this.summ=0;
			}
		}

	}
	cartBoxCountBox1(index,indexs){

		var count = this.refs[this.state.boxList[index].cartDetails[indexs].skuId].innerHTML;
		if(count==1){
			count=1;
		}else{
		count--;
		this.refs[this.state.boxList[index].cartDetails[indexs].skuId].innerHTML = count;
		if(this.count[index][indexs]==false){
			this.totals -= this.state.boxList[index].cartDetails[indexs].price*1.00
			this.setState({
				total:this.totals
			})
		}
		
		}
	}
	cartBoxCountBox3(index,indexs){
		var count = this.refs[this.state.boxList[index].cartDetails[indexs].skuId].innerHTML;
		count++;
		this.refs[this.state.boxList[index].cartDetails[indexs].skuId].innerHTML = count;
		if(this.count[index][indexs]==false){
			this.totals += this.state.boxList[index].cartDetails[indexs].price*1.00
			this.setState({
				total:this.totals
			})
		}		
	}
	edit(){
		for(let k=0;k<this.state.boxList.length;k++){
			this.sum[k]=true;
			this.refs[this.state.boxList[k]._id].setAttribute('src', 'data:image/png;base64,')
			for(let l=0;l<this.state.boxList[k].cartDetails.length;l++){
				this.count[k][l]=true;
				this.refs[this.state.boxList[k].cartDetails[l].skuId+"img"].setAttribute('src', 'data:image/png;base64,')

			}
		}
		this.refs.checkImg.setAttribute('src', 'data:image/png;base64,')
		this.totals=0;
		this.setState({
			isAll:true,
			isLittleAll:this.sum,
			isChoose:this.count,
			isEdit:!this.state.isEdit,
			total:this.totals,
			balance:true
		})
		if(this.state.isEdit==true){
			for(let i=0;i<this.state.boxList.length;i++){
				for(let j=0;j<this.state.boxList[i].cartDetails.length;j++){
					this.refs.statement.style.display="none"
					this.refs[this.state.boxList[i].cartDetails[j].skuId+"Box"].style.display="none"
				}
			}
			
		}else{
			for(let i=0;i<this.state.boxList.length;i++){
				for(let j=0;j<this.state.boxList[i].cartDetails.length;j++){
					this.refs.statement.style.display="block"
					this.refs[this.state.boxList[i].cartDetails[j].skuId+"Box"].style.display=""
				}
			}

		}
	}
	end(){
		var session = sessionStorage.getItem('username');
		var box = [];
		if(this.state.isEdit==true&&this.state.balance==false){
			alert("付款")
		}
		if(this.state.isEdit==false&&this.state.balance==false){
			for(let i=0;i<this.state.boxList.length;i++){
				for(let j=0;j<this.state.boxList[i].cartDetails.length;j++){
					
					if(this.state.isChoose[i][j]==false){
						box.push(this.state.isSkuId[i][j])
					}
				}
			}
			axios.get(`/api/removecart?username=${session}&skuId=${box}`).then(res=>

					this.props.history.push("/home")
			)				
		}
	}
}

export default Cart;